(function($){
 //var conf={'dvid':'#sh','dvid_bg':'#cccccc'};
 $.extend({
	over_lay:function (conf){
		if($(conf.dvid).css('display')=='none'){
			$(conf.dvid).css('display','block');
			$(conf.dvid).css('background',conf['dvid_bg']);
		}
	},
	over_close:function(conf){
		$(conf.dvid).css('display','none');
	}
});
})(jQuery);;
